import React, { useState, useEffect} from 'react'
import beckgroungTransperant from '../beckgroungTransperant.png'
import decoder from '../services/ImageDecoder'
import {popularity} from '../services/Popularity'


export default function PopularityPanel() {
    const [success, setSuccess] = useState(false)
    const [img, setImg] = useState("no image")



    useEffect(() => {
        const interval = setInterval(() => {
            if (img=="no image"){
                const image = getData()
                console.log(image)
                setImg(image[0])


            }

          }, 60)  
        return () => {
            setSuccess(true)
            clearInterval(interval)
        }
      }, [])     


    async function getData() {
     const image = popularity()
     var strr = [];
     popularity().then(function(response){
            strr.push(response.data);
     })


     .catch(function(error){
            console.log(error);
        });
     return strr;
    console.log(image)

    }



    return (
        <div style={{backgroundImage: `url(${beckgroungTransperant})`}}>
            <h1><span>Popularity Data!</span></h1>
            <h2 style={{fontSize: 20}} className="pt-running-text">
            here you can view data the popular routes
            </h2> 
            {success && <img src={{img}}></img>}
           {/* <p style={{color: '#0B7478', fontSize: 22}}>This server was published on XXX - see the paper <a href="">here</a></p> */}
        </div>

    )}
